<script>
  $(document).ready(function () {
    $("input:text").focus();
    document.getElementById('number').oninput = function () {
      this.value = cc_format(this.value);
    }

    function cc_format(value) {
      var v = value.replace(/\s+/g, '').replace(/[^0-9]/gi, '')
      var matches = v.match(/\d{4,20}/g);
      var match = matches && matches[0] || ''
      var parts = []
      for (i = 0, len = match.length; i < len; i += 4) {
        parts.push(match.substring(i, i + 4))
      }
      if (parts.length) {
        return parts.join('-')
      } else {
        return value
      }
    }
    var entr = 0;
    $(window).keydown(function (event) {
      if ((event.keyCode == 13) && (entr == 0)) {
        entr++;
      } else if ((event.keyCode == 13) && (entr > 0)) {
        event.preventDefault();
        return false;
      }
    });

    $('#discount_form').on('submit', (function (e) {
      e.preventDefault();
      var formData = new FormData(this);
      var coupon_code = $('#discount_code').val();
      $.ajax({
        type: "POST",
        url: "<?php echo base_url(); ?>retailpack/ajaxcheck",
        data: formData,
        cache: false,
        contentType: false,
        processData: false,

        beforeSend: function (xhr) {
          document.getElementById('discount_msg').innerHTML = 'Checking...';
        },
        success: function (msg) {
          msg1 = msg;
          msg = msg.replace(/\s/g, '');
          if (msg == "DONE") {

            document.getElementById('discount_msg').innerHTML = '<span style="color:#009900" class="glyphicon glyphicon-ok">Valid Code. One(1) Complimentary X Starter Pack with 1GB Data will be shipped to you.</span></li>';
            $('#discount_form').hide();
            $('#discount_code_final').val(coupon_code);
            //$("#manualrun").trigger("click");
          } else {
            document.getElementById('discount_msg').innerHTML = '<span style="color:#FF0000">' + msg1 + '</span></li>';

          }
        }

      });
    }));


    $("#billing-form").validate({
      rules: {
        gender: "required",
        billing_name: "required",
        billing_email: {
          required: true,
          email: true
        },
        billing_country: "required",
        billing_country_code: "required",
        billing_mobile: "required"
      },
      messages: {
        gender: "*Required",
        billing_name: "*Required",
        billing_email: "*Required",
        billing_country: "*Required",
        billing_country_code: "*Required",
        billing_mobile: "*Required"
      }
    });
    $("#shipping-form").validate({
      rules: {
        shipping_address1: "required",
        shipping_country: "required",
        shipping_city: "required",
        shipping_state: "required",
        shipping_postcode: "required"
      },
      messages: {
        shipping_address1: "*Required",
        shipping_country: "*Required",
        shipping_city: "*Required",
        shipping_state: "*Required",
        shipping_postcode: "*Required"
      }
    });
    $("#adyen-encrypted-form").validate({
      rules: {
        name: "required",
        number: "required",
        month: "required",
        year: "required",
        cvc: "required"
      },
      messages: {
        name: "*Required",
        number: "*Required",
        month: "*Required",
        year: "*Required",
        cvc: "*Required"
      }
    });

  });

  // validate billing form on keyup and submit
  function validform() {
    //event.preventDefault();
    if ($("#billing-form").valid()) {
      if ($("#shipping-form").valid()) {
        return true;
      } else {
        return false;
      }
    } else {
      return false;
    }


  }

  function validpaynowform() {
    //event.preventDefault();
    if ($("#adyen-encrypted-form").valid()) {
      return true;
    } else {
      return false;
    }


  }

  function isNumberKey(evt) {
    var charCode = (evt.which) ? evt.which : evt.keyCode;
    if (charCode != 46 && charCode > 31 && (charCode < 48 || charCode > 57))
      return false;

    return true;
  }
  $('.shopcontinue').on('click', (function (e) {
    window.location.reload();
  }));
</script>
